mapper.select:
SELECT imedia, date
FROM t_media
WHERE iuser = #{iuser}
AND date_format(date, '%Y-%m') = #{ym}
AND del = 0

SELECT imedia, pic FROM t_media_pics
WHERE imedia in (

    #{imedia}

)

SELECT A.imedia, A.date, A.star, B.pic
FROM t_media A
JOIN (
SELECT B.imedia, A.pic
FROM t_media_pics A
JOIN t_media B
ON A.imedia = B.imedia
WHERE B.iuser = #{iuser}
AND B.date = #{date}
GROUP BY A.imedia
) B
ON A.imedia = B.imedia
WHERE A.iuser = #{iuser}
AND A.date = #{date}
AND del = 0

SELECT imedia, title, comment, date, issaw, star, genrepk as genrePk
FROM t_media
WHERE imedia = #{imedia}
AND iuser = #{iuser}
AND del = 0

mapper.insert:
INSERT INTO t_media
SET iuser = #{iuser}
, title = #{title}
, genrepk = #{genrepk}
, date = #{date}

    , star = #{star}


    , comment = #{comment}

, is_saw = #{isSaw}

mapper.update:
UPDATE t_media
SET is_saw = #{isSaw}
WHERE imedia = #{imedia}
AND iuser = #{iuser}

