mapper.insert:
INSERT INTO t_media
SET iuser = #{iuser}
, title = #{title}
, genrepk = #{genrepk}
, date = #{date}

    , star = #{star}


    , comment = #{comment}

, is_saw = #{isSaw}

mapper.update:
UPDATE t_media
SET

    genrepk = #{genrepk}


    ,title = #{title}


    ,date = #{date}


    ,comment = #{comment}


    ,star = #{star}


    ,is_saw = #{isSaw}

WHERE imedia = #{imedia}
AND iuser = #{iuser}

UPDATE t_media
SET is_saw = #{isSaw}
WHERE imedia = #{imedia}
AND iuser = #{iuser}

mapper.select:
SELECT imedia, DATE
FROM t_media
WHERE iuser = #{iuser}
AND MONTH(date) = #{ym}
AND del = 0

SELECT imedia, pic FROM t_media_pics
WHERE imedia in (

    #{imedia}

)

SELECT imedia
FROM t_media
WHERE iuser = #{iuser}
AND imedia = #{imedia}
AND del = 0

SELECT imedia, title, comment, date, issaw, star, genrepk as genrePk
